@import "../../scaffolding.scss";
@import "../../variables.scss";


.site-list {
    font-weight: bold;
  }
  
  .site-list a {
    display: block;
    padding: 12px 21px;
    padding-bottom: 13px;
  
    font-size: 18px;
    line-height: 24px;
    color: #ffffff;
    text-transform: uppercase;
    text-decoration: none;
  
    background-color: #826550;
    border-top: 1px solid #886e5b;
    border-bottom: 1px solid #71543f;
  }
  
  .site-list a[href] {
    &:hover {
      background-color: #755a46;
      border-top-color: #755a46;
    }
  
    &:active {
      color: rgba(255, 255, 255, 0.3);
      background-color: #6a4d38;
      border-top-color: #6a4d38;
    }
  }
  
  
  
  @media (min-width: $tablet-width) {
  
    
    .site-list {
      display: flex;
      flex-wrap: wrap;
      justify-content: flex-start;
  
      width: 570px;
    }
    
    .site-list__item {
      margin-right: 32px;
  
      &:last-child {
        margin-right: 0;
      }
      
      &--active {
        position: relative;
  
        &::after {
          content: "";
  
          position: absolute;
          right: 0;
          bottom: -3px;
          left: 0;
  
          height: 3px;
  
          background: #826550;
        }
      }
    }
  
    .site-list__item a {
      padding: 0;
      padding-top: 25px;
      padding-bottom: 23px;
      font-size: 18px;
  
      background: none;
      border: none;
    }
  
    .site-list__item a[href] {
      &:hover {
        color: #826550;
  
        background-color: #000000;
      }
  
      &:active {
        color: #ffffff;
  
        background-color: #000000;
        opacity: 0.3;
      }
    }
  }
  
  
  
  @media (min-width: $desktop-width) {
  
    
    .site-list {  
      width: 780px;
    }
    
    .site-list__item {
      margin-right: 0;
  
      &--active::after {
        right: 30px;
        left: 30px;
      }
    }
  
    .site-list__item a {
      padding-right: 31px;
      padding-left: 31px;
      font-size: 14px;
    }
  
    .site-list__item a[href] {
      &:hover {
        color: #000000;
  
        background-color: #ffffff;
      }
  
      &:active {
        color: rgba(0, 0, 0, 0.3);
  
        background-color: #ffffff;
        opacity: 1;
      }
    }
  }

